package eth_test

import (
	"encoding/json"
	"github.com/INFURA/eth/pkg/eth"
	"github.com/stretchr/testify/require"
	"testing"
)

func TestTransactionContractCreation(t *testing.T) {
	// This is a transaction creating a contract
	payload := `{"blockHash":"0x774c34a19ff36b1d3669c54fe385502ad32862ed728da53b72b771f82c08b474","blockNumber":"0x6e2e24","from":"0x6b59210ade46b62b25e82e95ab390a7ccadd4c3a","gas":"0x2dc6c0","gasPrice":"0x1dcd65000","hash":"0xa6c1da26576bc41a722a3058bb1fa36e9c58188bd960bc1f40a976b6cb25e15e","input":"","nonce":"0x5f1f","r":"0xbe9872010301ebd71ea659b54b5d8b9f117249c9a5c23ef65de21c7807c7ddc8","s":"0x883971fb706e2336928a21be320c98c457238c2ef3466ba437f2f0bbfe32ad7","to":null,"transactionIndex":"0x6","v":"0x26","value":"0x0"}`

	tx := eth.Transaction{}
	err := json.Unmarshal([]byte(payload), &tx)
	require.NoError(t, err)
	require.Nil(t, tx.To)
}
